import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MapPin, Navigation } from "lucide-react";

interface Park {
  id: string;
  name: string;
  overview: string;
  difficulty: string;
  coordinates?: { lat: number; lng: number };
  mapUrl: string;
}

interface InteractiveMapProps {
  parks: Park[];
}

const parkCoordinates: Record<string, { lat: number; lng: number }> = {
  macritchie: { lat: 1.3489, lng: 103.8265 },
  bukittimah: { lat: 1.3547, lng: 103.7776 },
  railcorridor: { lat: 1.3635, lng: 103.7644 },
  coneyisland: { lat: 1.4118, lng: 103.9303 },
  sungeibuloh: { lat: 1.4453, lng: 103.7299 },
  labrador: { lat: 1.2718, lng: 103.8024 },
};

export default function InteractiveMap({ parks }: InteractiveMapProps) {
  const [selectedPark, setSelectedPark] = useState<string | null>(null);

  const selectedParkData = parks.find((p) => p.id === selectedPark);

  const centerLat = 1.3521;
  const centerLng = 103.8198;

  const getMapUrl = (parkId: string | null) => {
    if (!parkId) {
      return `https://www.google.com/maps/embed/v1/place?key=&q=Singapore&center=${centerLat},${centerLng}&zoom=11`;
    }
    const coords = parkCoordinates[parkId];
    if (coords) {
      return `https://www.google.com/maps/embed/v1/place?key=&q=${coords.lat},${coords.lng}&center=${coords.lat},${coords.lng}&zoom=14`;
    }
    return `https://www.google.com/maps/embed/v1/place?key=&q=Singapore&center=${centerLat},${centerLng}&zoom=11`;
  };

  return (
    <div className="w-full">
      <div className="grid md:grid-cols-3 gap-6">
        <div className="md:col-span-2 order-2 md:order-1">
          <Card className="overflow-hidden shadow-lg">
            <CardContent className="p-0">
              <div className="relative w-full" style={{ paddingBottom: "75%" }}>
                <div className="absolute inset-0 bg-muted/20 flex items-center justify-center">
                  <div className="text-center p-8">
                    <MapPin className="w-16 h-16 text-primary mx-auto mb-4" />
                    <h3 className="text-xl font-semibold text-foreground mb-2">
                      {selectedParkData ? selectedParkData.name : "Select a Park"}
                    </h3>
                    <p className="text-muted-foreground mb-4">
                      {selectedParkData
                        ? "Click 'View on Google Maps' to see directions"
                        : "Choose a park from the list to view its location"}
                    </p>
                    {selectedParkData && (
                      <Button
                        asChild
                        size="lg"
                        data-testid={`button-open-map-${selectedParkData.id}`}
                      >
                        <a
                          href={selectedParkData.mapUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          <Navigation className="w-4 h-4 mr-2" />
                          View on Google Maps
                        </a>
                      </Button>
                    )}
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="md:col-span-1 order-1 md:order-2">
          <Card className="shadow-lg">
            <CardContent className="p-4">
              <h3 className="text-lg font-semibold text-foreground mb-4 flex items-center gap-2">
                <MapPin className="w-5 h-5 text-primary" />
                Park Locations
              </h3>
              <div className="space-y-2 max-h-96 overflow-y-auto">
                {parks.map((park) => (
                  <button
                    key={park.id}
                    onClick={() => setSelectedPark(park.id)}
                    className={`w-full text-left p-3 rounded-lg border transition-all hover-elevate active-elevate-2 ${
                      selectedPark === park.id
                        ? "bg-primary/10 border-primary"
                        : "bg-background border-border"
                    }`}
                    data-testid={`button-select-park-${park.id}`}
                  >
                    <div className="flex items-start gap-2">
                      <MapPin
                        className={`w-4 h-4 mt-1 flex-shrink-0 ${
                          selectedPark === park.id ? "text-primary" : "text-muted-foreground"
                        }`}
                      />
                      <div className="flex-1 min-w-0">
                        <div className="font-medium text-foreground text-sm mb-1">
                          {park.name}
                        </div>
                        <Badge
                          variant={
                            park.difficulty === "Easy"
                              ? "secondary"
                              : park.difficulty === "Moderate"
                                ? "default"
                                : "destructive"
                          }
                          className="text-xs"
                        >
                          {park.difficulty}
                        </Badge>
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertContactMessageSchema, insertParkReviewSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  app.post("/api/contact", async (req, res) => {
    try {
      const validatedData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(validatedData);
      res.status(201).json({ success: true, message });
    } catch (error) {
      console.error("Contact form error:", error);
      res.status(400).json({ success: false, error: "Invalid contact form data" });
    }
  });

  app.post("/api/reviews", async (req, res) => {
    try {
      const validatedData = insertParkReviewSchema.parse(req.body);
      const review = await storage.createParkReview(validatedData);
      res.status(201).json({ success: true, review });
    } catch (error) {
      console.error("Review submission error:", error);
      res.status(400).json({ success: false, error: "Invalid review data" });
    }
  });

  app.get("/api/reviews/:parkId", async (req, res) => {
    try {
      const { parkId } = req.params;
      const reviews = await storage.getParkReviews(parkId);
      res.json({ success: true, reviews });
    } catch (error) {
      console.error("Get reviews error:", error);
      res.status(500).json({ success: false, error: "Failed to fetch reviews" });
    }
  });

  app.get("/api/reviews", async (req, res) => {
    try {
      const reviews = await storage.getAllReviews();
      res.json({ success: true, reviews });
    } catch (error) {
      console.error("Get all reviews error:", error);
      res.status(500).json({ success: false, error: "Failed to fetch reviews" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

client/src/pages/Reviews.tsx.content:
import { useEffect, useState } from "react";
import Layout from "@/components/Layout";
import ReviewSection from "@/components/ReviewSection";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface Park {
  id: string;
  name: string;
  difficulty: string;
}

export default function Reviews() {
  const [parks, setParks] = useState<Park[]>([]);
  const [selectedPark, setSelectedPark] = useState<string | null>(null);

  useEffect(() => {
    fetch("/data/parks.json")
      .then((res) => res.json())
      .then((data) => {
        setParks(data);
        if (data.length > 0) {
          setSelectedPark(data[0].id);
        }
      })
      .catch((err) => console.error("Failed to load parks data:", err));
  }, []);

  const selectedParkData = parks.find((p) => p.id === selectedPark);

  return (
    <Layout>
      <div className="bg-gradient-to-b from-secondary/30 to-background">
        <div className="container mx-auto px-4 py-12 md:py-16">
          <div className="text-center mb-12">
            <h1 className="text-4xl md:text-5xl font-bold text-foreground mb-4" data-testid="text-page-title">
              Park Reviews & Ratings
            </h1>
            <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
              Read visitor experiences and share your own reviews of Singapore's nature parks
            </p>
          </div>

          <div className="max-w-6xl mx-auto">
            <Card className="mb-8">
              <CardHeader>
                <CardTitle>Select a Park</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-3">
                  {parks.map((park) => (
                    <button
                      key={park.id}
                      onClick={() => setSelectedPark(park.id)}
                      className={`p-3 rounded-lg border text-left transition-all hover-elevate active-elevate-2 ${
                        selectedPark === park.id
                          ? "bg-primary/10 border-primary"
                          : "bg-background border-border"
                      }`}
                      data-testid={`button-select-park-${park.id}`}
                    >
                      <div className="font-medium text-sm text-foreground mb-2">
                        {park.name}
                      </div>
                      <Badge
                        variant={
                          park.difficulty === "Easy"
                            ? "secondary"
                            : park.difficulty === "Moderate"
                              ? "default"
                              : "destructive"
                        }
                        className="text-xs"
                      >
                        {park.difficulty}
                      </Badge>
                    </button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {selectedParkData && (
              <ReviewSection
                parkId={selectedParkData.id}
                parkName={selectedParkData.name}
              />
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
}

